mapper.select:
SELECT DISTINCT m.menu_id, m.parent_id, m.menu_name, m.menu_en_name, m.path, m.component, m.visible, m.status, CASE WHEN m.perms IS NULL THEN '' ELSE m.perms END AS perms, m.is_frame, m.is_cache, m.menu_type, m.icon, m.order_num, m.create_time,m.plugin_theme,m.menu_classify from sys_menu m left join sys_role_menu rm on m.menu_id = rm.menu_id left join sys_user_role ur on rm.role_id = ur.role_id left join sys_role ro on ur.role_id = ro.role_id where ur.user_id = #{params.userId}
AND menu_name like concat('%', #{menuName}, '%')
AND visible = #{visible}
AND status = #{status}
order by m.parent_id, m.order_num

select m.menu_id from sys_menu m left join sys_role_menu rm on m.menu_id = rm.menu_id where rm.role_id = #{roleId}
and m.menu_id not in (select m.parent_id from sys_menu m inner join sys_role_menu rm on m.menu_id = rm.menu_id and rm.role_id = #{roleId})
order by m.parent_id, m.order_num
